// Server-side tagging setup
gtag('config', 'G-XXXXXXXXXX', {
  transport_url: 'https://[your-gtm-server].com',
  first_party_collection: true,
  // Enhanced measurement
  enhanced_measurement: {
    file_downloads: true,
    outbound_clicks: true,
    scroll_tracking: true,
    video_tracking: true,
    form_interactions: true
  },
  // Custom dimensions
  custom_map: {
    'dimension1': 'user_type',
    'dimension2': 'client_id'
  }
});

// A/B Test Configuration
window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('config', 'OPT-XXXXXXXX', {
  experiments: [{
    id: 'my_experiment',
    variant: 0,  // Control
    trafficCoverage: 0.8,  // 80% of traffic
    customParameters: {
      userType: 'returning',
      deviceType: 'mobile'
    }
  }]
});
